import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";

export const Products = () => {

    const [ data, setData] = useState([]);
    const [filter, setFilter ] = useState(data);
    const [ loading, setLoading ] = useState(false);

    let componentMounted = true;

    useEffect(() => {
        const getProducts = async () => {
        setLoading(true);
        const response = await fetch("https://api.noroff.dev/api/v1/online-shop/")
        if(componentMounted) {
            setData(await response.clone().json())
            setFilter(await response.json());
            setLoading(false);
          console.log(filter)
        }

        return () => {
            componentMounted = false;
        } 

    }
     getProducts();
}, []);

const Loading = () => {
    return (
        <>
        Loading..
        </>
    )
}

const ShowProducts = () => {
    return (
        <>
    <div>
        <Button className="btn btn-outline-dark bg-outline-dark">Electronics</Button>
        <Button className="btn btn-outline-dark bg-dark">Cosmetics</Button>
        <Button className="btn btn-outline-dark bg-dark">Toys</Button>
        <Button className="btn btn-outline-dark bg-dark">Clothes</Button>

    </div>
    </>
    )
}





        return (
            <div className="container my-3 py-1">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center">Latest Products</h1>
                        <hr />

                    </div>
                </div>
                <div className="row justify-content-center">
                    {loading ? <Loading />  : <ShowProducts />}
                </div>
            </div>

        )




}




export default Products;
