import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { AsyncTypeahead } from "react-bootstrap-typeahead";

const SEARCH_URI = 'https://api.noroff.dev/api/v1/online-shop/';

export const Products = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const letsNavigate = useNavigate();

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      const response = await fetch("https://api.noroff.dev/api/v1/online-shop/");
      const products = await response.json();
      setData(products);
      setLoading(false);
    };
    getProducts();
  }, []);

  const Loading = () => {
    return <>Loading..</>;
  };

  const handleSearch = (query) => {
    if (query) {
      setLoading(true);
      fetch(`${SEARCH_URI}?q=${query}&per_page=50`)
        .then((resp) => resp.json())
        .then(({ items }) => {
          setSearchResults(items);
          setLoading(false);
        });
    } else {
      setSearchResults([]);
    }
  };

  const ShowProducts = () => {
    const productsToDisplay = searchQuery ? searchResults : data;

    return (
      <>
        <Container className="d-flex align-items-center">
          <Row>
            <Col className="col-md-12">
              <Form className="search d-flex col-8" onSubmit={handleSubmit}>
                <Form.Control
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Form>
            </Col>
          </Row>
        </Container>
        {productsToDisplay.map((product) => {
          return (
            <button
              onClick={() => letsNavigate(`/singleproduct/${product.id}`)}
              className="col-md-3 mg-5 p-3"
              key={product.id}
            >
              <div className="card h-100 text-center p-4">
                <img src={product.imageUrl} height="250px" alt={product.title}></img>
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text">NOK {product.price},-</p>
                </div>
              </div>
            </button>
          );
        })}
      </>
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="container my-3 py-1">
      <div className="row">
        <div className="col-12 mb-5">
          <h1 className="display-6 fw-bolder text-center">Latest Products</h1>
          <hr />
        </div>
      </div>
      <div className="row justify-content-center">
        {loading ? <Loading /> : <ShowProducts />}
      </div>
    </div>
  );
};

export default Products;
