import styled from "styled-components";

export const MainButton = styled.button`

background-color: black;
color: white;
padding 5px 40px;
cursor: pointer;
border-radius: 18px;
transition: background-color linear 800ms;


:hover {
  background-color: red;
  color: white;
}


  @keyframes fade {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
`;
