import React, { useContext } from "react";
import { CartItem } from "../cart/CartItem";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { StoreContext } from "../../context/StoreContext";

export const ShoppingCart = () => {
  const { cartItems, getTotalCartAmount, products } = useContext(StoreContext);
  const totalAmount = getTotalCartAmount();
  const navigate = useNavigate();

  // Helper function to filter products in the cart
  const getCartProducts = () => {
    return products.filter((product) => cartItems[product.id] > 0);
  };

  const cartProducts = getCartProducts();

  return (
    <div className="Cart">
      <div>
        <h1>Your Cart items:</h1>
      </div>

      <div className="CartItems">
        {cartProducts.length > 0 ? (
          cartProducts.map((product) => (
            <div key={product.id}>
              <CartItem data={product} />
            </div>
          ))
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      {totalAmount > 0 ? (
        <div className="checkoutCart">
          <p>Subtotal: NOK {getTotalCartAmount()},-</p>
          <Button onClick={() => navigate("/")}>Continue shopping</Button>
          <Button className="bg-dark" onClick={() => navigate("/checkout")}>
            Checkout
          </Button>
        </div>
      ) : (
        <h1>Your Cart is empty</h1>
      )}
    </div>
  );
};
