import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col } from "react-bootstrap";

export const Products = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const letsNavigate = useNavigate();

  let componentMounted = true;

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      const response = await fetch("https://api.noroff.dev/api/v1/online-shop/");
      if (componentMounted) {
        const jsonData = await response.json();
        setData(jsonData);
        setLoading(false);
        setSearchResults(jsonData);
      }
    };

    getProducts();

    return () => {
      componentMounted = false;
    };
  }, []);

  const Loading = () => {
    return <div>Loading...</div>;
  };

  const SearchBar = ({ setSearchResults }) => {
    const handleSubmit = (e) => e.preventDefault();

    const handleSearchChange = (e) => {
      if (!e.target.value) return setSearchResults(data);

      const resultsArray = data.filter(
        (product) =>
          product.title.includes(e.target.value) ||
          product.tags.includes(e.target.value)
      );

      setSearchResults(resultsArray);
    };

    return (
      <header>
        <form className="search" onSubmit={handleSubmit}>
          <input
            className="search-input"
            type="text"
            id="search"
            onChange={handleSearchChange}
            placeholder="Search products..."
          />
          <button className="search-button"></button>
        </form>
      </header>
    );
  };

  const ShowProducts = () => {
    return (
      <>
        <Container>
          <Row>
            <Col>
              <SearchBar setSearchResults={setSearchResults} />
            </Col>
          </Row>
        </Container>
        {searchResults.map((product) => {
          return (
            <div className="col-md-3 mg-5 p-3" key={product.id}>
              <div className="card h-100 text-center p-4">
                <img
                  src={product.imageUrl}
                  height="250px"
                  alt={product.title}
                ></img>
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text">NOK {product.price},-</p>
                </div>
                <button
                  onClick={() => letsNavigate(`/singleproduct/${product.id}`)}
                  className="btn btn-outline-dark"
                >
                  + Buy now
                </button>
              </div>
            </div>
          );
        })}
      </>
    );
  };

  return (
    <div className="container my-3 py-1">
      <div className="row">
        <div className="col-12 mb-5">
          <h1 className="display-6 fw-bolder text-center">Latest Products</h1>
          <hr />
        </div>
      </div>
      <div className="row justify-content-center">
        {loading ? <Loading /> : <ShowProducts />}
      </div>
    </div>
  );
};

export default Products;
