import React, { useContext } from "react";
import { Button } from "react-bootstrap";
import { StoreContext } from "../../context/StoreContext";



export let CartItem = (props) => {

    let { id, title, price, imgUrl } = props.data;
    const { cartItems, addToCart, removeFromCart, updateCartItemCount  } = useContext(StoreContext)
    return (
        <div className="cart-item">
       <div classname="countHandler">
               <Button onClick={() => removeFromCart(id, title, price, imgurl)}>-</Button>
               <input value={String(cartItems[id])} onChange={(e) => updateCartItemCount(Number(e.target.value), id)} />
               <Button onClick={() => addToCart(id)}>+</Button>
           </div>
        </div>

    )
}
