import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Row, Container, Form } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { Typeahead } from "react-bootstrap-typeahead";
import "react-bootstrap-typeahead/css/Typeahead.css";
import { Icon } from "@iconify/react";

const SEARCH_URI = "https://api.noroff.dev/api/v1/online-shop/";

export const Search = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedOption, setSelectedOption] = useState([]);

  const letsNavigate = useNavigate();

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      try {
        const response = await fetch(`${SEARCH_URI}?q=${searchQuery}`);
        if (response.ok) {
          const products = await response.json();
          setData(products);
          setFilteredProducts(products);
        } else {
          console.error("Failed to fetch products:", response.statusText);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    };

    getProducts();
  }, [searchQuery]);

  const Loading = () => {
    return <div>Loading...</div>;
  };

  const handleSelection = (selected) => {
    if (selected.length > 0) {
      const selectedProduct = filteredProducts.find(
        (product) => product.title === selected[0]
      );
      letsNavigate(`/singleproduct/${selectedProduct.id}`);
    }
  };

  const searchOptions = filteredProducts.map((product) => product.title);

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-10 mb-5"></div>
      </div>
      <div className="row justify-content-center">
        {loading ? (
          <Loading />
        ) : (
          <Container className="d-flex align-items-center">
            <Row>
              <Form
                className="search d-flex col-8"
                onSubmit={(e) => e.preventDefault()}
              >
               <Button  style={{ width: "3rem", height: "3rem", position: "relative" }}
                variant="outline-dark"
                className="rounded-circle">
                    <Icon icon="prime:search" height="30px" />
                </Button>
                <Typeahead
                  id="product-search"
                  options={searchOptions}
                  selected={selectedOption}
                  onChange={handleSelection}
                  placeholder="Search products..."
                />
              </Form>
            </Row>
          </Container>
        )}
      </div>
    </div>
  );
};

export default Search;
