import { Button, Container, Navbar as NavbarBs, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Icon } from "@iconify/react";
import { Search } from "./SearchBar";
import { StoreContext } from "../context/StoreContext";
import { useContext } from "react";

export function NavBar() {
  const { getTotalCartAmount } = useContext(StoreContext);
  const totalAmount = getTotalCartAmount();
  return (
    <Container>
      <NavbarBs
        sticky="top"
        className="navbar navbar-expand-lg bg-white shadow-sm justify-content-between"
      >
        <div className="me-auto my-2 my-lg-0">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0 pt-3">
            <li className="d-flex col-6">
              <Link to="/" style={{ paddingLeft: 13, textDecoration: "none" }}>
                <Button
                  style={{
                    width: "3rem",
                    height: "3rem",
                    position: "relative",
                  }}
                  variant="outline-dark"
                  className="rounded-circle"
                >
                  <Icon icon="prime:home" height="30px" />
                </Button>
              </Link>
            </li>
            <li className="navbar-search d-flex col-8">
              <Search />
            </li>
            <li className="d-flex justify-content-end">
              <Link to="/Contact">
                <Button
                  style={{
                    width: "3rem",
                    height: "3rem",
                    position: "relative",
                  }}
                  variant="outline-dark"
                  className="rounded-circle"
                >
                  <Icon icon="prime:envelope" height="30px" />
                </Button>
              </Link>
            </li>

            <li className="d-flex justify-content-end">
              <Link to="/ShoppingCart">
                <Button
                  style={{
                    width: "3rem",
                    height: "3rem",
                    position: "relative",
                  }}
                  variant="outline-dark"
                  className="rounded-circle"
                >
                  <Icon
                    icon="prime:shopping-cart"
                    height="30px"
                    style={{ height: "1.5rem" }}
                  />
                  <div
                    className="rounded-circle d-flex justify-content-center bg-danger align-items-center"
                    style={{
                      color: "white",
                      width: "1.5rem",
                      height: "1.5rem",
                      position: "absolute",
                      bottom: 0,
                      right: 0,
                      transform: "translate(25%, 25%)",
                    }}
                  >
                    {totalAmount}
                  </div>
                </Button>
              </Link>
            </li>
          </ul>
        </div>
      </NavbarBs>
    </Container>
  );
}

export default NavBar;
