import React, { useContext } from "react";
import { CartItem } from "./CartItem";
import { useNavigate } from "react-router-dom";
import { StoreContext } from "../../context/StoreContext";
import { formatCurrency } from "../../utilities/formatCurrency";


export const ShoppingCart = () => {
  const { cartItems, products, clearCart } = useContext(StoreContext);
  const navigate = useNavigate();

  const totalAmount = Object.keys(cartItems).reduce((total, productId) => {
    const product = products.find((p) => p.id === productId);
    const quantity = cartItems[productId];
    if (product && quantity > 0) {
      total += quantity * product.price;
    }
    return total;
  }, 0);

  return (
    <div className=" p-5" style={{ border: "1px solid black" }}>
      <div className="p-2 text-center display-6 fw-bolder">
        <h1 className=" display-6 fw-bolder">Your Cart items:</h1>
        <hr />
      </div>
      <div>
      <button
                className="btn btn-outline-dark"
                onClick={() => {
                  clearCart();

                }}
              >

              </button>
      </div>

      <div className="CartItems">
        {Object.keys(cartItems).map((productId) => {
          const product = products.find((p) => p.id === productId);

          if (product && cartItems[productId] > 0) {
            return (
              <div key={productId}>
                <strong className="d-flex text-center">{product.title}</strong>
                <div>
                  <img
                    src={product.imageUrl}
                    height="130px"
                    alt={product.title}
                  />
                  <br />

                  <br />
                  <p>Price: NOK {formatCurrency(product.price)},-</p>
                  <CartItem data={product} />
                  <strong>
                    Total Price: NOK{" "}
                    {formatCurrency(product.price * cartItems[productId])},-
                  </strong>
                </div>
                <hr />
              </div>
            );
          }
          return null;
        })}
      </div>

      {totalAmount === 0 ? (
        <h1>Your Cart is empty</h1>
      ) : (
        <div>
          <hr />
          <div
            className="d-flex flex-row p-3"
            style={{ border: "1px solid black" }}
          >
            <div className="px-5 bg-white pt-3">
            <strong>Subtotal: {formatCurrency(totalAmount)},-</strong>
            </div>

            <div className="p-1">
              <button
                className="btn btn-outline-dark"
                onClick={() => {
                  clearCart();
                  navigate("/checkout");
                }}
              >
                Checkout
              </button>
            </div>
            <div className="p-1">
              <button
                className="btn btn-outline-dark"
                onClick={() => navigate("/")}
              >
                Continue shopping
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
