import React, { createContext, useState, useEffect } from "react";
import { Products } from "../data/FetchProducts";

export const StoreContext = createContext();

const getDefaultCart = () => {
  let cart = {};
  for (let i = 1; i < Products.length; i++) {
    cart[i] = 0;
  }
  return cart;
};
export const StoreContextProvider = (props) => {
    const Product = (props) => {
    const { id , title, price, productImg} = props.data;
    const { addToCart, cartItems } = useContext(StoreContext)   

    const cartItemAmount = cartItems[id];

    return  (
       <div className="product">
        <h3>{title}</h3>
         <img src={productImg} height="200px" alt="product"></img>
         <p>NOK {price},-</p>
         <Button className="addToCartBtn bg-dark" onClick={() => addToCart(id)}>Add to cart { cartItemAmount > 0 && <>({cartItemAmount})</>}</Button>
     </div>
     )

}
  const [cartItems, setCartItems] = useState(getDefaultCart());
  const [products, setProducts] = useState([]);

  console.log(cartItems);

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await fetch(
          "https://api.noroff.dev/api/v1/online-shop/"
        );
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    }

    fetchProducts();
  }, []);

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = products.find((product) => product.id === Number(item));
        totalAmount += cartItems[item] * itemInfo.price;
      }
    }
    return totalAmount;
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    console.log(addToCart);
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const updateCartItemCount = (newAmount, itemId) => {
    newAmount = Number(newAmount);

    if (isNaN(newAmount)) {
      return;
    }

    setCartItems((prev) => ({ ...prev, [itemId]: newAmount }));
  };

  const contextValue = {
    cartItems,
    products,
    addToCart,
    removeFromCart,
    updateCartItemCount,
    getTotalCartAmount,
  };

  return (
    <StoreContext.Provider value={contextValue}>
      {props.children}
    </StoreContext.Provider>
  );
};

export default StoreContextProvider;
