import styled from "styled-components";

export const Button = styled.button`
transform: scale(1);
background-color: white;
border: 2px solid black;
border-radius: 6px;
padding: 10px 20px;
cursor: pointer;
transition: background-color linear 800ms;

:hover {
    transform: scale(1.2);
  background-color: black;
  color: white;
}

`;
