import React, { useContext, useEffect, useState } from "react";
import { CartItem } from "../cart/CartItem";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { StoreContext } from "../../context/StoreContext";

export const ShoppingCart = () => {
  const { cartItems, getTotalCartAmount } = useContext(StoreContext);
  const navigate = useNavigate();
  const [products, setProducts] = useState([]); // State variable to hold product data
  const [isLoading, setIsLoading] = useState(true); // State variable to track loading state

  // Fetch product data when the component mounts
  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await fetch(`https://api.noroff.dev/api/v1/online-shop/${id}`);
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      } finally {
        setIsLoading(false);
      }
    }

    fetchProducts();
  }, []);

  const totalAmount = getTotalCartAmount();

  return (
    <div className="Cart">
      <div>
        <h1>Your Cart items:</h1>
      </div>
      <div className="CartItems">
        {isLoading ? (
          // Display a loading indicator while fetching product data
          <p>Loading...</p>
        ) : (
            Object.keys(cartItems).map((productId) => {
                const product = products.find((p) => p.id === parseInt(productId));
                if (cartItems[productId] !== 0) {
                  return <CartItem key={productId} data={product} />;
                }
                return null;
              })

        )}
      </div>
      {totalAmount > 0 ? (
        <div className="checkoutCart">
          <p>Subtotal: NOK {getTotalCartAmount()},-</p>
          <Button onClick={() => navigate("/")}>Continue shopping</Button>
          <Button className="bg-dark" onClick={() => navigate("/checkout")}>
            Checkout
          </Button>
        </div>
      ) : (
        <h1>Your Cart is empty</h1>
      )}
    </div>
  );
};
