  <div>
    <Nav cart={cart} emptyCart={emptyCart} />
    <Grid centered stackable padded relaxed>
                <Grid.Column className='left-column' width={5}>
                    <LeftPanel />
                </Grid.Column>
                <Grid.Column width={9}>
                    <Products
                        addItemToCart={addItemToCart} 
                    />
                </Grid.Column>
            </Grid>

        </div>
  );
}
