import React, { useContext } from "react";
import { StoreContext } from "../../context/StoreContext";

export let CartItem = (props) => {
  const { id, title, imageUrl } = props.data;
  const { addToCart, removeFromCart, updateCartItemCount, cartItems } = useContext(StoreContext);

  const quantity = cartItems[id]; // Access the quantity from cartItems using the product's id

  return (
    <div className="cart-item">
      <div className="countHandler">
        <button onClick={() => removeFromCart(id)}>-</button>
        <input
          value={String(quantity)} // Use the 'quantity' variable
          onChange={(e) => updateCartItemCount(Number(e.target.value), id)}
        />
        <button onClick={() => addToCart(id)}>+</button>
      </div>
    </div>
  );
};
