import React from "react";
import { PRODUCTS } from "./products";
import { Product} from "./product";
import { Button } from "react-bootstrap";

export const Store = ()  => {
    return <div classname="store">
        <div className="storeTitle">
            <h1>Mokkis e-com store</h1>
        </div>
        {Product.map((post, i) => (
            <Button
            className="bg-light text-dark-emphasis "
             key={i}
             >
        <div>
          <img src={post.productImg} height="200px" alt={post.title} />
          <h2>{post.title}</h2>
           <p>Price: {post.price}</p>
          <p>Discounted price: {post.discountedPrice}</p>
         </div>
         <Button 
         onClick={() => ({ type: 'addProduct', payload: post })} className="bg-dark">+ Add to cart</Button>
        </Button>
      ))}
       </div>

            }
