import React from 'react'
import { FontAwesomeIcon } from "@fontawesome/react-fontawesome";
import { faMagnifyingGlass} from "@fontawesome/free-solid-svg-icons"

const SearchBar = ({ data, setSearchResults }) => {

    const handleSubmit = (e) => e.preventDefault()

    const handleSearchChange = (e) => {
        if(!e.target.value) return setSearchResults(data)

        const resultsArray = data.filter(data = data.title.includes(e.target.value)) 
    }

  return (
    <header>
        <form className='search' onSubmit={handleSubmit}>
            <input className='search-input'
            type="text"
            id="search"
            onChange={handleSearchChange} />



        </form>
    </header>
  )
}

export default SearchBar
